import abc

class ChartGridLine (  XlsChartGridLine) :
    """

    """
    @property

    def Border(self)->'ChartBorder':
        """
    <summary>
        Gets line border. Read only.
    </summary>
        """
        GetDllLibXls().ChartGridLine_get_Border.argtypes=[c_void_p]
        GetDllLibXls().ChartGridLine_get_Border.restype=c_void_p
        intPtr = CallCFunction(GetDllLibXls().ChartGridLine_get_Border, self.Ptr)
        ret = None if intPtr==None else ChartBorder(intPtr)
        return ret


