class DataConnections (SpireObject) :
    """

    """

    def get_Item(self ,index:int)->'ExternalConnection':
        """

        """
        
        GetDllLibXls().DataConnections_get_Item.argtypes=[c_void_p ,c_int]
        GetDllLibXls().DataConnections_get_Item.restype=c_void_p
