from enum import Enum
from plum import dispatch
from typing import TypeVar,Union,Generic,List,Tuple
from spire.xls.common import *
from spire.xls import *
from ctypes import *
import abc

class HPageBreak (  XlsHPageBreak) :
    """

    """
    @property

    def Location(self)->'CellRange':
        """
    <summary>
        Location of the page break.
    </summary>
        """
        GetDllLibXls().HPageBreak_get_Location.argtypes=[c_void_p]
        GetDllLibXls().HPageBreak_get_Location.restype=c_void_p
        intPtr = CallCFunction(GetDllLibXls().HPageBreak_get_Location, self.Ptr)
        ret = None if intPtr==None else CellRange(intPtr)
        return ret


    @Location.setter
    def Location(self, value:'CellRange'):
        GetDllLibXls().HPageBreak_set_Location.argtypes=[c_void_p, c_void_p]
        CallCFunction(GetDllLibXls().HPageBreak_set_Location, self.Ptr, value.Ptr)

